import { useEffect, useState } from "react"
// components
import ListingPopup from "../components/Popup"

const ListingDetails = ({ residency }) => {

    var id = residency._id

    const specificResidency = () => {
        const [specificResidency, setSpecificResidencies] = useState(null)

        useEffect(() => {
            const fetchSpecifcListing = async () => {
                const response = await fetch('http://localhost:8000/api/residency/' + id)
                const json = await response.json()

                if (response.ok) {
                    setSpecificResidencies(json)
                }
            }

            fetchSpecifcListing
        }, [])

        return (
            <div>
                <ListingPopup residency={specificResidency} />
            </div>
        )
    }

    return (
        <div className="listing-details">
            <h4>{residency.title}</h4>
            <p><strong>Location: </strong>{residency.address} , {residency.city}</p>
            <p>{residency.description}</p>
            <p><strong>Price: Â£{residency.price}</strong></p>
            <p>{residency.createdAt}</p>
            <img id="myImg" alt="Cover" src={residency.image}></img>
            <button onClick={specificResidency(residency._id)}>View More</button>
        </div>
    )
}

export default ListingDetails
